import React, { Component } from "react";
import axios from "axios";

class Description extends Component {
  state = { project_title: "", project_info: "" };

  constructor(props) {
    super(props);
    this.getinfo();
  }

  getinfo = () => {
    var temp = String(this.props.match.params.id);
    console.log(temp);
    temp = "?movie_id=" + temp;
    axios.get("https://yts.mx/api/v2/movie_details.json" + temp).then((res) => {
      console.log(res.data.data.movie.description_full);
      this.setState({
        project_title: res.data.data.movie.description_full,
      });
    });
  };

  render() {
    return (
      <React.Fragment>
        <h1 style={{ color: "white" }}>{this.state.project_title}</h1>
        <h1 style={{ color: "white" }}>{this.state.project_info}</h1>
      </React.Fragment>
    );
  }
}
export default Description;
