import React, { Component } from "react";
import { Redirect } from "react-router-dom";

class NavBar extends Component {
  state = {
    search: "",
  };

  updateSearch = (e) => {
    var s = e.target.value;

    this.setState({ search: s });
    // console.log(this.state.search);
  };

  render() {
    return (
      <React.Fragment>
        <div className="header_navbar navbar navbar-expand-lg">
          <button
            class="navbar-toggler"
            type="button"
            data-toggle="collapse"
            data-target="#navbarSupportedContent"
            aria-controls="navbarSupportedContent"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span class="navbar-toggler-icon"></span>
          </button>
          <img
            id="logo"
            className="header_elements"
            src="https://i.ibb.co/JtvsWhn/tarunlogo.png"
          ></img>
          <div className="header_elements">
            <a className="navhome" href="/">
              Home
            </a>
          </div>

          <div
            className="header_elements collapse navbar-collapse"
            id="navbarSupportedContent"
          >
            <form
              action={"/search/" + this.state.search}
              className="form-inline my-2 my-lg-0"
              id="searchform"
            >
              <input
                onChange={this.updateSearch}
                className="form-control mr-sm-2"
                id="search"
                type="search"
                placeholder="Search"
                aria-label="Search"
              />
            </form>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default NavBar;
