import React, { Component } from "react";
import axios from "axios";

class Description extends Component {
  state = { project_title: "" };

  constructor(props) {
    super(props);
    this.getinfo();
  }

  getinfo = () => {
    const temp = String(this.props.match.params.id);
    axios
      .get("http://localhost:5000/info/" + temp)
      .then((res) => this.setState({ project_title: res.data.project_title }));
  };

  render() {
    return (
      <React.Fragment>
        <h1>{this.state.project_title}</h1>
        {console.log(this.state.project_title)}
      </React.Fragment>
    );
  }
}
export default Description;
