import React, { Component } from "react";
import Navbar from "./navbar";
import axios from "axios";

class Query extends Component {
  state = {
    movies: [],
  };

  constructor(props) {
    super(props);
  }
  componentDidMount() {
    // your source code to load initial data
    this.getinfo();
  }
  // componentDidUpdate() {
  //   this.getinfo();
  // }

  getinfo = () => {
    var query = String(this.props.match.params.query);
    // console.log(query);
    var search =
      "https://yts.mx/api/v2/list_movies.json?limit=50&query_term=" + query;

    axios.get(search).then((res) => {
      // console.log(res.data.data.movies);
      this.setState({
        movies: res.data.data.movies.map((movies) => movies),
      });
    });
    window.location.reload();
  };
  render() {
    return (
      <React.Fragment>
        <Navbar />
        {this.state.movies.map((url) => (
          <img src={url.medium_cover_image}></img>
        ))}
      </React.Fragment>
    );
  }
}

export default Query;
