import React, { Component } from "react";
import Videos from "./videos";
import axios from "axios";

class VideoHelper extends Component {
  state = {
    imgurls: [
      "https://images-eu.ssl-images-amazon.com/images/S/pv-target-images/5fad7caad087e191828d44f0fb63953124eecf0a66177b776486b33514e20b92._UR1920,1080_RI_SX712_FMwebp_.jpg",
      "https://images-eu.ssl-images-amazon.com/images/S/pv-target-images/8ce674e475b4a3ac06ca288b073669feb3536638671b3815acbb73900a91d382._UR1920,1080_RI_SX712_FMwebp_.jpg",
      "https://images-eu.ssl-images-amazon.com/images/S/pv-target-images/620025162bd93ec708a6bbd33dad70a71ef920923267fd10453f8998cf5aa63a._UR1920,1080_RI_SX712_FMwebp_.jpg",
      "https://images-eu.ssl-images-amazon.com/images/S/pv-target-images/b35817423cbfa8cc362d0604ceb87ec880a14ca6b1c7dd5185a97cc0d8c2bb8a._UR1920,1080_RI_SX712_FMwebp_.png",
      "https://images-eu.ssl-images-amazon.com/images/S/pv-target-images/12cfd45ace899cc26a58d25df4cb5934464dac006f8f8dbcfbdebd5e58f56520._UR1920,1080_RI_SX712_FMwebp_.jpg",
      "https://images-eu.ssl-images-amazon.com/images/S/pv-target-images/775d2271c07165c5753e787cc1ddfbbf47c622a648b93737ca28d88218b53045._UR1920,1080_RI_SX712_FMwebp_.jpg",
      "https://images-eu.ssl-images-amazon.com/images/S/atv-aps-images/encoded/GULABO/IN/en_US/COVER_ART/TITLE._UR1920,1080_RI_SX712_FMwebp_.jpg",
      "https://images-eu.ssl-images-amazon.com/images/S/atv-aps-images/encoded/UPLOAD_S1/en_US/COVER_ART/CLEAN/GLASSES._UR1920,1080_RI_SX712_FMwebp_.jpg",
      "https://images-eu.ssl-images-amazon.com/images/S/atv-aps-images/encoded/AERO/en_US/COVER_ART/CLEAN/BLUE._UR1920,1080_RI_SX712_FMwebp_.jpg",
    ],
    leftMargin: 20,
  };

  getinfo = () => {
    axios.get("http://localhost:5000/info/").then((res) => {
      const urls = res.data.map((data) => console.log(data.url_photo));
      this.setState({ imgurls: String(urls) });
      console.log(this.state.imgurls);
    });
  };

  handleLeft = () => {
    this.setState({
      leftMargin:
        this.state.leftMargin - 800 > -1600 ? this.state.leftMargin - 800 : 20,
    });
  };

  handleRight = () => {
    this.setState({
      leftMargin:
        this.state.leftMargin + 800 > 20 ? 20 : this.state.leftMargin + 800,
    });
  };

  render() {
    return (
      <React.Fragment>
        <Videos
          imgurls={this.state.imgurls}
          leftMargin={this.state.leftMargin}
          slideleft={this.handleLeft}
          slideright={this.handleRight}
        />
      </React.Fragment>
    );
  }
}

export default VideoHelper;
