import React, { Component } from "react";
import Navbar from "./navbar";
import axios from "axios";

class Query extends Component {
  state = {};

  constructor(props) {
    super(props);
    this.getinfo();
  }

  getinfo = () => {
    var genre = String(this.props.match.params.query);
    console.log(genre);
    var search =
      "https://yts.mx/api/v2/list_movies.json?limit=50&sort_by=like_count&" +
      "genre=" +
      genre;

    axios
      .get("https://yts.mx/api/v2/list_movies.json?limit=50&sort_by=like_count")
      .then((res) => {
        console.log(res.data.data.movies);
        this.setState({
          imgurls: res.data.data.movies.map((movies) => movies),
        });
      });
  };
  render() {
    return (
      <React.Fragment>
        <Navbar />
      </React.Fragment>
    );
  }
}

export default Query;
